import streamlit as st
from src.password_generator import RandomPasswordGenerator , MemorablePasswordGenerator , PinCodeGenerator


st.image("data:image/png;base64,",width=100)
st.title(":zap: Password Generator")

option = st.radio(
    "select a password generator",
    ("Random Password","Memorable Password","Pin Code")
)
if option == 'Pin Code':
    length = st.slider("select the length of the pin code",4,100)
    generator = PinCodeGenerator(length)

elif option == 'Random Password':
    length = st.slider("select the length of the pin code",4,100)
    include_number = st.toggle("Includ Number")
    include_symbol = st.toggle("Include Symbol")
    generator=RandomPasswordGenerator(length,include_number,include_symbol)
    
elif option == 'Memorable Password':
    num_of_words = st.slider("select number of words",4,100)
    separator = st.text_input("Separator", value = '-')
    capitalization = st.toggle("Capitalization")
    generator = MemorablePasswordGenerator(num_of_words , separator , capitalization)
    
password =generator.generate()
st.write(fr"your password is ``` {password} ```")
